import React, { useEffect, useState } from "react";
import Popup from "./components/Popup";

function App() {
  const [showPopup, setShowPopup] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState(""); // تعريف حالة كلمة المرور

  useEffect(() => {
    const storedUsername = localStorage.getItem("username");
    if (storedUsername) {
      setUsername(storedUsername);
    } else {
      setShowPopup(true);
    }
  }, []);

  const handleRegister = (credentials) => {
    setUsername(credentials.username);
    setPassword(credentials.password); // حفظ كلمة المرور
    setShowPopup(false);
  };

  const handleLogin = (credentials) => {
    setUsername(credentials.username);
    setPassword(credentials.password); // حفظ كلمة المرور
    setShowPopup(false);
  };

  const handleClose = () => {
    setShowPopup(false);
  };

  const handleLogout = () => {
    localStorage.removeItem("username");
    localStorage.removeItem("password");
    setUsername("");
    setPassword(""); // إعادة تعيين كلمة المرور
    setShowPopup(true);
  };

  const handleGenerateNewAccount = async () => {
    try {
      const response = await fetch("/api/generate-credentials/");
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      setUsername(data.username);
      setPassword(data.password); // حفظ كلمة المرور الجديدة
    } catch (error) {
      console.error("Error generating new account:", error);
    }
  };

  return (
    <div className="App">
      {showPopup ? (
        <Popup
          onRegister={handleRegister}
          onClose={handleClose}
          onGenerateNewAccount={handleGenerateNewAccount}
          onLogin={handleLogin}
        />
      ) : (
        <>
          <h1>Welcome back, {username}!</h1>
          <button onClick={handleLogout}>Logout</button>
          <h1>Welcome to CoDikty</h1>
        </>
      )}
    </div>
  );
}

export default App;
