// App.js

import React, { useEffect, useState } from "react";
import "./App.css";
import Popup from "./components/Popup";

function App() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [showPopup, setShowPopup] = useState(true);

  useEffect(() => {
    const storedUsername = localStorage.getItem("username");
    const storedPassword = localStorage.getItem("password");
    if (storedUsername && storedPassword) {
      setUsername(storedUsername);
      setPassword(storedPassword);
      setShowPopup(false);
    }
  }, []);

  const handleRegister = (credentials) => {
    setUsername(credentials.username);
    setPassword(credentials.password);
    if (credentials.remember) {
      localStorage.setItem("username", credentials.username);
      localStorage.setItem("password", credentials.password);
    }
    setShowPopup(false);
  };

  const handleGenerateNewAccount = async () => {
    try {
      const response = await fetch("/api/generate-credentials/");
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      setUsername(data.username);
      setPassword(data.password);
    } catch (error) {
      console.error("Error generating new account:", error);
    }
  };

  const handleLogin = (credentials) => {
    setUsername(credentials.username);
    setPassword(credentials.password);
    setShowPopup(false);
  };

  const handleLogout = () => {
    setUsername("");
    setPassword("");
    localStorage.removeItem("username");
    localStorage.removeItem("password");
    setShowPopup(true);
    handleGenerateNewAccount();
  };

  return (
    <div className="App">
      {showPopup ? (
        <Popup
          username={username}
          password={password}
          onRegister={handleRegister}
          onGenerateNewAccount={handleGenerateNewAccount}
          onLogin={handleLogin}
          onClose={() => setShowPopup(false)}
        />
      ) : (
        <>
          <h1>Welcome back, {username}!</h1>
          <button onClick={handleLogout}>Logout</button>
          <h1>Welcome to CoDikty</h1>
        </>
      )}
    </div>
  );
}

export default App;
