import React, { useEffect, useState } from "react";
import "./Popup.css"; // تأكد من إضافة ملف CSS

const Popup = ({ onRegister, onClose }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [remember, setRemember] = useState(false);

  useEffect(() => {
    const generateRandomCredentials = async () => {
      try {
        const response = await fetch("/api/generate-credentials/");
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        setUsername(data.username);
        setPassword(data.password);
      } catch (error) {
        console.error("Error fetching credentials:", error);
      }
    };
    generateRandomCredentials();
  }, []);

  const handleRegister = () => {
    if (remember) {
      localStorage.setItem("username", username);
      localStorage.setItem("password", password);
    }
    onRegister({ username, password });
  };

  return (
    <div className="popup-overlay">
      <div className="popup">
        <div className="popup-content">
          <h2>Welcome to CoDikty</h2>
          <p>Your temporary username and password:</p>
          <div>
            <label>Username:</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div>
            <label>Password:</label>
            <input
              type="text"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div>
            <input
              type="checkbox"
              id="remember"
              checked={remember}
              onChange={() => setRemember(!remember)}
            />
            <label htmlFor="remember">Remember me</label>
          </div>
          <button onClick={handleRegister}>Register</button>
          <button onClick={onClose}>Close</button>
        </div>
      </div>
    </div>
  );
};

export default Popup;
