import React, { useEffect, useState } from "react";
import "./Popup.css";

const Popup = ({ onRegister, onClose, onGenerateNewAccount, onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [remember, setRemember] = useState(false);
  const [isExistingUser, setIsExistingUser] = useState(false);

  useEffect(() => {
    const storedUsername = localStorage.getItem("username");
    const storedPassword = localStorage.getItem("password");
    if (storedUsername && storedPassword) {
      setUsername(storedUsername);
      setPassword(storedPassword);
      setIsExistingUser(true);
    } else {
      generateRandomCredentials();
    }
  }, []);

  const generateRandomCredentials = async () => {
    try {
      const response = await fetch("/api/generate-credentials/");
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      setUsername(data.username);
      setPassword(data.password);
      setIsExistingUser(false);
    } catch (error) {
      console.error("Error fetching credentials:", error);
    }
  };

  const handleRegister = () => {
    if (remember) {
      localStorage.setItem("username", username);
      localStorage.setItem("password", password);
    }
    onRegister({ username, password, remember });
  };

  const handleGenerateNewAccount = () => {
    generateRandomCredentials();
  };

  const handleLogin = () => {
    onLogin({ username, password });
  };

  return (
    <div className="popup-overlay">
      <div className="popup">
        <div className="popup-content">
          <h2>Welcome to CoDikty</h2>
          <p>Your temporary username and password:</p>
          <div>
            <label>Username:</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              disabled={isExistingUser}
            />
          </div>
          <div>
            <label>Password:</label>
            <input
              type="text"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              disabled={isExistingUser}
            />
          </div>
          <div>
            <input
              type="checkbox"
              id="remember"
              checked={remember}
              onChange={() => setRemember(!remember)}
            />
            <label htmlFor="remember">Remember me</label>
          </div>
          {isExistingUser ? (
            <button onClick={handleLogin}>Login</button>
          ) : (
            <button onClick={handleRegister}>Register</button>
          )}
          <button onClick={onClose}>Close</button>
          <button onClick={handleGenerateNewAccount}>
            Generate New Account
          </button>
        </div>
      </div>
    </div>
  );
};

export default Popup;
